// Préparer les paramètres pour PostgreSQL
const item = $input.first().json;

console.log("=== PRÉPARATION PARAMÈTRES ===");

let originalRequest = $('Keep Request').first().json.request_original;
let projectId = $('Keep Request').first().json.project_id;
let requestId = $('Keep Request').first().json.request_id;

// Extraire l'analyse depuis Gemini
let analysis = {};
if (item.candidates && item.candidates[0]?.content?.parts[0]?.text) {
  try {
    const textContent = item.candidates[0].content.parts[0].text;
    analysis = JSON.parse(textContent);
    console.log("✅ Analyse parsée depuis Gemini");
  } catch(e) {
    console.error("❌ Erreur parsing Gemini:", e.message);
    analysis = {
      analysis: { type: "FEATURE", complexity: "M", priority: "MEDIUM", confidence: 0.8 },
      orchestration: { agents_needed: ["PM", "US", "DEV"], estimated_hours: 24, parallel_possible: false },
      next_steps: ["Générer User Stories", "Développer la fonctionnalité", "Tester"],
      risks: ["Parse error - analyse par défaut"]
    };
  }
}

// Préparer les paramètres avec fallbacks corrects (jamais UNKNOWN)
const params = [
  originalRequest,
  analysis.analysis?.type || "FEATURE",                 // FEATURE par défaut (jamais UNKNOWN)
  analysis.analysis?.complexity || "M",                 
  analysis.analysis?.priority || "MEDIUM",              
  Number(analysis.analysis?.confidence) || 0.5,         
  JSON.stringify(analysis.orchestration?.agents_needed || ["PM", "US", "DEV"]), // US inclus par défaut
  Number(analysis.orchestration?.estimated_hours) || 24, 
  Boolean(analysis.orchestration?.parallel_possible),   
  JSON.stringify(analysis.next_steps || ["Analyser les besoins", "Générer les spécifications"]),
  JSON.stringify(analysis.risks || ["Risque non identifié"]),
  JSON.stringify(analysis),
  $execution?.id || "unknown",
  projectId,
  requestId
];

console.log("✅ Paramètres préparés pour PostgreSQL");
console.log("Type:", params[1], "| Agents:", params[5]);

return [{ 
  json: {
    params: params,
    request_text: originalRequest,
    analysis_type: analysis.analysis?.type || "FEATURE",
    complexity: analysis.analysis?.complexity || "M",
    confidence: analysis.analysis?.confidence || 0.5
  }
}];

