// Fusionner les donnees pour Gemini
const inputData = $('Keep Input Data').first().json;
let cadrageInfo = 'Pas de cadrage disponible';

// Si le projet a un cadrage, l extraire
if ($('Fetch Project Cadrage').all().length > 0) {
  const projectData = $('Fetch Project Cadrage').first().json;
  if (projectData.project_cadrage) {
    const cadrage = projectData.project_cadrage;
    cadrageInfo = '\nPerimetre: ' + JSON.stringify(cadrage.perimetre, null, 2) + '\nArchitecture proposee: ' + JSON.stringify(cadrage.architecture, null, 2) + '\nEstimation: ' + (cadrage.estimation?.effort_jours || 'N/A') + ' jours\n';
  }
}

return [{
  json: {
    request_text: inputData.request_text,
    complexity: inputData.complexity,
    cadrage_info: cadrageInfo,
    analysis_id: inputData.analysis_id,
    cadrage_id: inputData.cadrage_id || null
  }
}];

