parameters.jsCode:
// Parser la reponse Gemini
const geminiResponse = $input.first().json;
const mergedData = $('Merge Data for Gemini').first().json;

console.log('=== AGENT USER STORIES ===');
console.log('Analysis ID:', mergedData.analysis_id);

let userStories = {};
try {
  const textContent = geminiResponse.candidates[0].content.parts[0].text;
  userStories = JSON.parse(textContent);
  console.log('OK ' + (userStories.stories?.length || 0) + ' User Stories generees');
  console.log('Total points:', userStories.metrics?.total_points || 0);
} catch(e) {
  console.error('ERROR Erreur parsing Gemini:', e.message);
  userStories = {
    epic: { title: 'Erreur de parsing', goal: 'N/A' },
    stories: [],
    sprint_suggestion: {},
    metrics: { total_points: 0, must_have_points: 0, should_have_points: 0, could_have_points: 0, estimated_sprints: 0 }
  };
}

// Preparer pour la DB
const stories = userStories.stories || [];
const acceptanceCriteria = stories.length > 0 ? stories.map(s => s.acceptance_criteria || []).flat() : [];
const dependencies = stories.length > 0 ? stories.map(s => ({ id: s.id, deps: s.dependencies || [] })) : [];

const params = [
  mergedData.analysis_id,
  mergedData.cadrage_id || null,
  JSON.stringify(stories),
  JSON.stringify(acceptanceCriteria),
  userStories.metrics?.total_points || 0,
  JSON.stringify(userStories.sprint_suggestion || {}),
  JSON.stringify(dependencies)
];

return [{ 
  json: { 
    params,
    user_stories: userStories,
    analysis_id: mergedData.analysis_id,
    summary: {
      total_stories: stories.length,
      total_points: userStories.metrics?.total_points || 0,
      must_have: stories.filter(s => s.priority === 'MUST').length,
      estimated_sprints: userStories.metrics?.estimated_sprints || 0
    }
  }
}];

